CREATE TABLE `tb_novels` (
  `id_novel` varchar(30) NOT NULL,
  `judul_novel` varchar(50) NOT NULL,
  `gambar_novel` varchar(50) NOT NULL,
  `author_novel` varchar(50) NOT NULL,
  `status_novel` varchar(30) NOT NULL,
  `genre_novel` varchar(100) NOT NULL,
  `deskripsi_novel` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

